import React, {Component} from 'react';
import axios from 'axios';

export default class Slika extends Component {

constructor(props) {
    super(props);
    this.state({
        slika: ''
    })
}

componentDidMount() {
    axios
    .get('https://dog.ceo/api/breeds/image/random')
    .then( res => {this.setState({
        slika: res.data[0].message
    })})
}

render() {

    let divStyle = {
        backgroundImage: this.state.slika, 
        backgroundSize: 'cover',
        height: '300px', 
        width: '300px'
    }

    return (
        <div className="slika" style={divStyle}>
        </div>
    )
}

}
